mapper.insert:
INSERT
  INTO TBL_MEMBER
(
  MEMBER_NO
, MEMBER_ID
, MEMBER_PWD
, NICKNAME
, PHONE
, EMAIL
, ADDRESS
)
VALUES
(
  SEQ_MEMBER_NO.NEXTVAL
, #{ memberId }
, #{ memberPwd }
, #{ nickname }
, #{ phone }
, #{ email }
, #{ address }
)

mapper.update:
UPDATE
      TBL_MEMBER
  SET NICKNAME = #{ nickname }
    , PHONE = #{ phone }
    , EMAIL = #{ email }
    , ADDRESS = #{ address }
WHERE MEMBER_STATUS = 'Y'
  AND MEMBER_NO = #{ memberNo }
